import React from "react";
import {
  Chart as ChartJS,
  BarElement,
  LineElement,
  ArcElement,
  CategoryScale,
  LinearScale,
  PointElement,
  Tooltip,
  Legend
} from "chart.js";
import { Bar, Line, Pie } from "react-chartjs-2";

ChartJS.register(
  BarElement,
  LineElement,
  ArcElement,
  PointElement,
  CategoryScale,
  LinearScale,
  Tooltip,
  Legend
);

const generateColors = (count) => {
  const baseColors = [
    "#FF6384", "#36A2EB", "#FFCE56", "#8AFFC1", "#FF9F40",
    "#DDA0DD", "#87CEFA", "#32CD32", "#E6E6FA", "#90EE90"
  ];
  return Array.from({ length: count }, (_, i) => baseColors[i % baseColors.length]);
};

const ChartDisplay = ({ data, chartType }) => {
  if (!Array.isArray(data) || data.some(d => typeof d !== "number")) {
    return <p style={{ color: "red" }}>⚠️ Cannot display chart: output must be an array of numbers.</p>;
  }

  const labels = data.map((_, i) => `#${i + 1}`);
  const colors = generateColors(data.length);

  const chartData = {
    labels,
    datasets: [
      {
        label: "Result",
        data,
        backgroundColor: chartType === "pie" ? colors : "#36A2EB",
        borderColor: "#2b2b2b",
        borderWidth: 1
      }
    ]
  };

  const options = {
    responsive: true,
    plugins: {
      legend: { display: chartType === "pie" }
    }
  };

  switch (chartType) {
    case "bar":
      return <Bar data={chartData} options={options} />;
    case "line":
      return <Line data={chartData} options={options} />;
    case "pie":
      return <Pie data={chartData} options={options} />;
    default:
      return <p>Unsupported chart type</p>;
  }
};

export default ChartDisplay;
